    def "is document applicable with invalid GAMP category"() {
        given:
        def steps = Spy(PipelineSteps)
        def usecase = Mock(LeVADocumentUseCase)
        def scheduler = Spy(new LeVADocumentScheduler(steps, usecase))

        // Test Parameters
        def documentType = "myDocumentType"
        def phase = "myPhase"
        def stage = MROPipelineUtil.PipelinePhaseLifecycleStage.POST_START
        def project = createProject()
        project.capabilities << [ LeVADocs: [ GAMPCategory: "0" ] ]
        def repo = project.repositories.first()

        when:
        scheduler.isDocumentApplicable(documentType, phase, stage, project)

        then:
        def e = thrown(IllegalArgumentException)
        e.message == "Error: unable to assert applicability of document type '${documentType}' for project '${project.id}' in phase '${phase}'. The GAMP category '0' is not supported."

        when:
        scheduler.isDocumentApplicable(documentType, phase, stage, project, repo)

        then:
        e = thrown(IllegalArgumentException)
        e.message == "Error: unable to assert applicability of document type '${documentType}' for project '${project.id}' and repo '${repo.id}' in phase '${phase}'. The GAMP category '0' is not supported."
    }

